package org.traccar.fleet.web.rest;

import org.traccar.fleet.FleetApp;
import org.traccar.fleet.domain.Device;
import org.traccar.fleet.domain.Message;
import org.traccar.fleet.repository.DeviceRepository;
import org.traccar.fleet.repository.MessageRepository;
import org.traccar.fleet.service.MessageService;
import org.traccar.fleet.web.rest.dto.MessageDTO;
import org.traccar.fleet.web.rest.mapper.MessageMapper;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import static org.hamcrest.Matchers.hasItem;
import org.mockito.MockitoAnnotations;
import org.springframework.boot.test.IntegrationTest;
import org.springframework.boot.test.SpringApplicationConfiguration;
import org.springframework.http.MediaType;
import org.springframework.http.converter.json.MappingJackson2HttpMessageConverter;
import org.springframework.data.web.PageableHandlerMethodArgumentResolver;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.test.context.web.WebAppConfiguration;
import org.springframework.test.util.ReflectionTestUtils;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.setup.MockMvcBuilders;
import org.springframework.transaction.annotation.Transactional;

import javax.annotation.PostConstruct;
import javax.inject.Inject;
import java.time.Instant;
import java.time.ZonedDateTime;
import java.time.format.DateTimeFormatter;
import java.time.ZoneId;
import java.util.List;

import static org.assertj.core.api.Assertions.assertThat;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.*;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.*;


/**
 * Test class for the MessageResource REST controller.
 *
 * @see MessageResource
 */
@RunWith(SpringJUnit4ClassRunner.class)
@SpringApplicationConfiguration(classes = FleetApp.class)
@WebAppConfiguration
@IntegrationTest
public class MessageResourceIntTest extends FleetResourceIntTest {

    private static final DateTimeFormatter dateTimeFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd'T'HH:mm:ss.SSS'Z'").withZone(ZoneId.of("Z"));


    private static final ZonedDateTime DEFAULT_MESSAGE_TIME = ZonedDateTime.ofInstant(Instant.ofEpochMilli(0L), ZoneId.systemDefault());
    private static final ZonedDateTime UPDATED_MESSAGE_TIME = ZonedDateTime.now(ZoneId.systemDefault()).withNano(0);
    private static final String DEFAULT_MESSAGE_TIME_STR = dateTimeFormatter.format(DEFAULT_MESSAGE_TIME);

    private static final Integer DEFAULT_MACRO_NUMBER = 1;
    private static final Integer UPDATED_MACRO_NUMBER = 2;
    private static final String DEFAULT_MACRO_TEXT = "";
    private static final String UPDATED_MACRO_TEXT = "";

    @Inject
    private DeviceRepository deviceRepository;
    @Inject
    private MessageRepository messageRepository;
    @Inject
    private MessageMapper messageMapper;
    @Inject
    private MessageService messageService;
    @Inject
    private MappingJackson2HttpMessageConverter jacksonMessageConverter;
    @Inject
    private PageableHandlerMethodArgumentResolver pageableArgumentResolver;

    private MockMvc restMessageMockMvc;

    private Message message;
    private Device device;

    @PostConstruct
    public void setup() {
        MockitoAnnotations.initMocks(this);
        MessageResource messageResource = new MessageResource();
        ReflectionTestUtils.setField(messageResource, "messageService", messageService);
        ReflectionTestUtils.setField(messageResource, "messageMapper", messageMapper);
        this.restMessageMockMvc = MockMvcBuilders.standaloneSetup(messageResource)
            .setCustomArgumentResolvers(pageableArgumentResolver)
            .setMessageConverters(jacksonMessageConverter).build();
    }

    @Before
    public void initTest() {
        message = new Message();
        message.setMessageTime(DEFAULT_MESSAGE_TIME);
        message.setMacroNumber(DEFAULT_MACRO_NUMBER);
        message.setText(DEFAULT_MACRO_TEXT);

        device = initDevice();
        message.setDevice(device);
    }

    @Test
    @Transactional
    public void createMessage() throws Exception {
        int databaseSizeBeforeCreate = messageRepository.findAll().size();

        // Create the Message
        MessageDTO messageDTO = messageMapper.messageToMessageDTO(message);

        restMessageMockMvc.perform(post("/api/messages")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(messageDTO)))
                .andExpect(status().isCreated());

        // Validate the Message in the database
        List<Message> messages = messageRepository.findAll();
        assertThat(messages).hasSize(databaseSizeBeforeCreate + 1);
        Message testMessage = messages.get(messages.size() - 1);
        assertThat(testMessage.getMessageTime()).isEqualTo(DEFAULT_MESSAGE_TIME);
        assertThat(testMessage.getMacroNumber()).isEqualTo(DEFAULT_MACRO_NUMBER);
        assertThat(testMessage.getText()).isEqualTo(DEFAULT_MACRO_TEXT);
    }

    @Test
    @Transactional
    public void checkMessageTimeIsRequired() throws Exception {
        int databaseSizeBeforeTest = messageRepository.findAll().size();
        // set the field null
        message.setMessageTime(null);

        // Create the Message, which fails.
        MessageDTO messageDTO = messageMapper.messageToMessageDTO(message);

        restMessageMockMvc.perform(post("/api/messages")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(messageDTO)))
                .andExpect(status().isBadRequest());

        List<Message> messages = messageRepository.findAll();
        assertThat(messages).hasSize(databaseSizeBeforeTest);
    }

    @Test
    @Transactional
    public void getAllMessages() throws Exception {
        // Initialize the database
        messageRepository.saveAndFlush(message);

        // Get all the messages
        restMessageMockMvc.perform(get("/api/messages?sort=id,desc"))
                .andExpect(status().isOk())
                .andExpect(content().contentType(MediaType.APPLICATION_JSON))
                .andExpect(jsonPath("$.[*].id").value(hasItem(message.getId().intValue())))
                .andExpect(jsonPath("$.[*].messageTime").value(hasItem(DEFAULT_MESSAGE_TIME_STR)))
                .andExpect(jsonPath("$.[*].macroNumber").value(hasItem(DEFAULT_MACRO_NUMBER)))
                .andExpect(jsonPath("$.[*].text").value(hasItem(DEFAULT_MACRO_TEXT.toString())));
    }

    @Test
    @Transactional
    public void getMessage() throws Exception {
        // Initialize the database
        messageRepository.saveAndFlush(message);

        // Get the message
        restMessageMockMvc.perform(get("/api/messages/{id}", message.getId()))
            .andExpect(status().isOk())
            .andExpect(content().contentType(MediaType.APPLICATION_JSON))
            .andExpect(jsonPath("$.id").value(message.getId().intValue()))
            .andExpect(jsonPath("$.messageTime").value(DEFAULT_MESSAGE_TIME_STR))
            .andExpect(jsonPath("$.macroNumber").value(DEFAULT_MACRO_NUMBER))
            .andExpect(jsonPath("$.text").value(DEFAULT_MACRO_TEXT.toString()));
    }

    @Test
    @Transactional
    public void getNonExistingMessage() throws Exception {
        // Get the message
        restMessageMockMvc.perform(get("/api/messages/{id}", Long.MAX_VALUE))
                .andExpect(status().isNotFound());
    }

    @Test
    @Transactional
    public void updateMessage() throws Exception {
        // Initialize the database
        messageRepository.saveAndFlush(message);
        int databaseSizeBeforeUpdate = messageRepository.findAll().size();

        // Update the message
        Message updatedMessage = new Message();
        updatedMessage.setId(message.getId());
        updatedMessage.setMessageTime(UPDATED_MESSAGE_TIME);
        updatedMessage.setMacroNumber(UPDATED_MACRO_NUMBER);
        updatedMessage.setText(UPDATED_MACRO_TEXT);
        updatedMessage.setDevice(device);
        MessageDTO messageDTO = messageMapper.messageToMessageDTO(updatedMessage);

        restMessageMockMvc.perform(put("/api/messages")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(messageDTO)))
                .andExpect(status().isOk());

        // Validate the Message in the database
        List<Message> messages = messageRepository.findAll();
        assertThat(messages).hasSize(databaseSizeBeforeUpdate);
        Message testMessage = messages.get(messages.size() - 1);
        assertThat(testMessage.getMessageTime()).isEqualTo(UPDATED_MESSAGE_TIME);
        assertThat(testMessage.getMacroNumber()).isEqualTo(UPDATED_MACRO_NUMBER);
        assertThat(testMessage.getText()).isEqualTo(UPDATED_MACRO_TEXT);
    }

    @Test
    @Transactional
    public void deleteMessage() throws Exception {
        // Initialize the database
        messageRepository.saveAndFlush(message);
        int databaseSizeBeforeDelete = messageRepository.findAll().size();

        // Get the message
        restMessageMockMvc.perform(delete("/api/messages/{id}", message.getId())
                .accept(TestUtil.APPLICATION_JSON_UTF8))
                .andExpect(status().isOk());

        // Validate the database is empty
        List<Message> messages = messageRepository.findAll();
        assertThat(messages).hasSize(databaseSizeBeforeDelete - 1);
    }
}
